// These flags define which text to prefix to each log entry generated by the Logger.
const (
	// Bits or'ed together to control what's printed. There is no control over the
	// order they appear (the order listed here) or the format they present (as
	// described in the comments).  A colon appears after these items:
	//  2009/0123 01:23:23.123123 /a/b/c/d.go:23: message
	Ldate         = 1 << iota                     // the date: 2009/0123
	Ltime                                         // the time: 01:23:23
	Lmicroseconds                                 // microsecond resolution: 01:23:23.123123.  assumes Ltime.
	Llongfile                                     // full file name and line number: /a/b/c/d.go:23
	Lshortfile                                    // final file name element and line number: d.go:23. overrides Llongfile
	Lmodule                                       // module name
	Llevel                                        // level: 0(Debug), 1(Info), 2(Warn), 3(Error), 4(Panic), 5(Fatal)
	LstdFlags     = Ldate | Ltime | Lmicroseconds // initial values for the standard logger
	Ldefault      = Lmodule | Llevel | Lshortfile | LstdFlags
) // [prefix][time][level][module][shortfile|longfile]

const (
	Lnop = iota
	Ldebug
	Linfo
	Lwarn
	Lerror
	Lpanic
	Lfatal
)

const BUFFER_LEN = 1000

var levels = []string{
	"",
	"[DEBUG]",
	"[INFO]",
	"[WARN]",
	"[ERROR]",
	"[PANIC]",
	"[FATAL]",
}

// A Logger represents an active logging object that generates lines of
// output to an io.WriterCloser.  Each logging operation makes a single call to
// the Writer's Write method.  A Logger can be used simultaneously from
// multiple goroutines; it guarantees to serialize access to the Writer.
type Logger struct {
	mu         sync.Mutex // ensures atomic writes; protects the following fields
	prefix     string     // prefix to write at beginning of each line
	flag       int        // properties
	Level      int
	out        io.WriteCloser // destination for output
	buf        chan []byte    // for accumulating text to write
	isClosed   chan bool      // for accumulating text to write
	levelStats [6]int64

	//for rotate
	rotate       bool
	rotateLogger *RotateLogger
}

// New creates a new Logger.   The out variable sets the
// destination to which log data will be written.
// The prefix appears at the beginning of each generated log line.
// The flag argument defines the logging properties.
func New(out io.WriteCloser, prefix string, flag int) *Logger {
	logger := &Logger{out: out, prefix: prefix, buf: make(chan []byte, BUFFER_LEN), isClosed: make(chan bool), Level: 1, flag: flag}
	//go logger.RealWrite()
	go RealWrite(logger)
	return logger
}

func NewRotate(dir, prefix, suffix string, size int64) (*Logger, error) {
	kbSize := size * 1024 * 1024
	rl, err := NewRotateLogger(dir, prefix, suffix, kbSize)
	if err != nil {
		return nil, err
	}
	return &Logger{out: rl.logf, prefix: prefix, Level: 1, flag: Ldefault, rotate: true, rotateLogger: rl}, nil
}

var Std = New(os.Stderr, "", Ldefault)

func SetStd(std *Logger) {
	Std = std
}

var JournalLog *Logger

func SetJournalLog(jlog *Logger) {
	JournalLog = jlog
	JournalLog.Level = -1
}

// Cheap integer to fixed-width decimal ASCII.  Give a negative width to avoid zero-padding.
// Knows the buffer has capacity.
func itoa(buf *bytes.Buffer, i int, wid int) {
	var u uint = uint(i)
	if u == 0 && wid <= 1 {
		buf.WriteByte('0')
		return
	}

	// Assemble decimal in reverse order.
	var b [32]byte
	bp := len(b)
	for ; u > 0 || wid > 0; u /= 10 {
		bp--
		wid--
		b[bp] = byte(u%10) + '0'
	}

	// avoid slicing b to avoid an allocation.
	for bp < len(b) {
		buf.WriteByte(b[bp])
		bp++
	}
}

func moduleOf(file string) string {
	pos := strings.LastIndex(file, "/")
	if pos != -1 {
		pos1 := strings.LastIndex(file[:pos], "/src/")
		if pos1 != -1 {
			return file[pos1+5 : pos]
		}
	}
	return "UNKNOWN"
}

func (l *Logger) formatHeader(buf *bytes.Buffer, t time.Time, file string, line int, lvl int, reqId string) {
	if l.prefix != "" {
		buf.WriteString(l.prefix)
	}
	if l.flag&(Ldate|Ltime|Lmicroseconds) != 0 {
		if l.flag&Ldate != 0 {
			buf.WriteByte('[')
			year, month, day := t.Date()
			itoa(buf, year, 4)
			buf.WriteByte('-')
			itoa(buf, int(month), 2)
			buf.WriteByte('-')
			itoa(buf, day, 2)
			buf.WriteByte(' ')
		}
		if l.flag&(Ltime|Lmicroseconds) != 0 {
			hour, min, sec := t.Clock()
			itoa(buf, hour, 2)
			buf.WriteByte(':')
			itoa(buf, min, 2)
			buf.WriteByte(':')
			itoa(buf, sec, 2)
			if l.flag&Lmicroseconds != 0 {
				buf.WriteByte('.')
				itoa(buf, t.Nanosecond()/1e3, 6)
			}
			buf.WriteByte(']')
			buf.WriteByte(' ')
		}
	}
	if reqId != "" {
		buf.WriteByte('[')
		buf.WriteString(reqId)
		buf.WriteByte(']')
	}
	if l.flag&Llevel != 0 {
		buf.WriteString(levels[lvl])
		buf.WriteByte(' ')
	}
}

func (l *Logger) formatHeader2(t time.Time, lvl int, reqId string) string {
	prefix := l.prefix
	var (
		date  string
		clock string
		reqid string
		level string
		//source string
	)

	if l.flag&(Ldate|Ltime|Lmicroseconds) != 0 {
		if l.flag&Ldate != 0 {
			year, month, day := t.Date()
			date = fmt.Sprintf("[%d-%02d-%02d ", year, month, day)
		}
		if l.flag&(Ltime|Lmicroseconds) != 0 {
			hour, min, sec := t.Clock()
			t_clock := fmt.Sprintf("%02d:%02d:%02d", hour, min, sec)
			var t_ms string
			if l.flag&Lmicroseconds != 0 {
				t_ms = fmt.Sprintf(".%d", t.Nanosecond()/1e3)
			}
			clock = fmt.Sprintf("%s%s] ", t_clock, t_ms)
		}
	}
	if reqId != "" {
		reqid = "[" + reqId + "]"
	}
	if l.flag&Llevel != 0 {
		level = levels[lvl]
	}

	header := prefix + date + clock + reqid + level
	//TODO: 自定义skip数
	//pc, _, lineno, ok := runtime.Caller(6)
	//if ok {
	//	source := fmt.Sprintf("%s:%d ", runtime.FuncForPC(pc).Name(), lineno)
	//	header += source
	//}

	return header
}

func (l *Logger) RotateMode() bool {
	return l.rotate
}

func (l *Logger) Close() {

	if l.rotate {
		l.rotateLogger.Close()
	}
}

// Output writes the output for a logging event.  The string s contains
// the text to print after the prefix specified by the flags of the
// Logger.  A newline is appended if the last character of s is not
// already a newline.  Calldepth is used to recover the PC and is
// provided for generality, although at the moment on all pre-defined
// paths it will be 2.
func (l *Logger) Output(reqId string, lvl int, calldepth int, s string) error {

	if l.rotate {
		return l.rotateLogger.Output(reqId, lvl, calldepth, s)
	}
	if lvl < l.Level {
		return nil
	}

	now := time.Now() // get this early.
	l.levelStats[lvl]++
	hd := l.formatHeader2(now, lvl, reqId)
	content := hd + s
	if len(s) > 0 && s[len(s)-1] != '\n' {
		content = content + "\n"
	}
	l.buf <- []byte(content)
	return nil
}

func (l *Logger) RealWrite() {
	for buf := range l.buf {
		l.mu.Lock()
		l.out.Write(buf)
		l.mu.Unlock()
	}
}

func RealWrite(l *Logger) {
	for {
		select {
		case buf := <-l.buf:
			l.mu.Lock()
			l.out.Write(buf)
			l.mu.Unlock()
		case <-l.isClosed:
			for more := true; more; {
				select {
				case buf := <-l.buf:
					l.mu.Lock()
					l.out.Write(buf)
					l.mu.Unlock()
				default:
					more = false
				}
			}
			l.out.Close()
			return
		}
	}
}

// -----------------------------------------

// Printf calls l.Output to print to the logger.
// Arguments are handled in the manner of fmt.Printf.
func (l *Logger) Printf(format string, v ...interface{}) {
	l.Output("", Linfo, 2, fmt.Sprintf(format, v...))
}

// Print calls l.Output to print to the logger.
// Arguments are handled in the manner of fmt.Print.
func (l *Logger) Print(v ...interface{}) { l.Output("", Linfo, 2, fmt.Sprint(v...)) }

// Println calls l.Output to print to the logger.
// Arguments are handled in the manner of fmt.Println.
func (l *Logger) Println(v ...interface{}) { l.Output("", Linfo, 2, fmt.Sprintln(v...)) }

